import { useState } from 'react';

function LoginInput(props) {
    const {id, placeholder, labelText, value, onChange, type} = props;
    return (
        <div className='mb-3'>
            <label htmlFor={id} className='form-label'>{labelText}</label>
            <input onChange={onChange} required type={type} className="form-control" id={id} name={id} value={value} placeholder={placeholder} />
        </div>
    )
}

export default function LoginForm() {
    const [loginData, setLoginData] = useState({
        username: '',
        password: ''
    })

    const handleLogindata = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setLoginData({
            ...loginData,
            [inputName]: value
        });
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        const nloginData = new FormData();

        nloginData.append('username', loginData.username)
        nloginData.append('password')
        const response = await fetch('http://localhost:8000/api/token/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Accept': 'application/json',
            },
            body: JSON.stringify({
                username: loginData.username,
                password: loginData.password,
            }),
        });
        const data = await response.json()
        console.log("TOKEN:", data.access)

        localStorage.setItem('accessToken', data.access);
    }

    return (
        <>
        <form className='form' onSubmit={handleSubmit}>
            <LoginInput
                id="username"
                placeholder="username"
                labelText=" UserName"
                value={loginData.username}
                onChange={handleLogindata}
                type="text" />
            <LoginInput 
                id="password" 
                placeholder="Super Secret Password" 
                labelText="Password"
                value={loginData.password}
                onChange={handleLogindata}
                type="text" />
            <button type="submit" className="btn btn-primary">Login</button>
        </form>
        </>
    )
}
