import { useState } from 'react';
import './App.css';

function BoostrapInput(props) {
    const {id, placeholder, labelText, value, onChange, type} = props;
    return (
        <div className="mb-3">
        <label htmlFor={id} className="form-label">{labelText}</label>
        <input onChange={onChange} required type={type} className="form-control" id={id} name={id} value={value} placeholder={placeholder} />
    </div>
    )
}

function AccountForm(props) {
    const [nemail, nsetEmail] =useState()
    const [formData, setFormData] = useState({
        email: '',
        name: '',
        password: ''
    })

    const handleFormChange = (e) => {
        const value = e.target.value;
        const inputName = e.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        })
    }

    return (
        <>
        <form className='form'>
            <BoostrapInput 
                id="email" 
                placeholder="emailaddress@email.com" 
                labelText="Your email address"
                value={email}
                onChange={e => setEmail(e.target.value)}
                type="email" />
            <div className="mb-3">
                <label htmlFor="email" className="form-label">Email address</label>
                <input onChange={handleFormChange} required type="email" className="form-control" id="email" name="email" value={formData.email} placeholder="name@example.com" />
            </div>
            <div className="mb-3">
                <label htmlFor="name" className="form-label">Name</label>
                <input onChange={handleFormChange} required type="text" className="form-control" id="name" name="name" value={formData.name} placeholder="Your name here" />
            </div>
            <div className="mb-3">
                <label htmlFor="password" className="form-label">Password</label>
                <input onChange={handleFormChange} required type="password" className="form-control" id="password" name="password" value={formData.password} placeholder="Your password here" />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
        </>
    );
}

export default AccountForm;
